import Task from './Task'

const Tasks = ({ tasks, onDelete, onToggle }) => {
    return (
        // setTasks([...tasks, {}]) - to add a new task
        <>
            {tasks.map((task, index) => (
                <Task key={index} task={task} 
                onDelete={onDelete} onToggle={onToggle} />
            ))}    
        </>
    )
}

export default Tasks
