import Task from './Tasks'

const Tasks = ({ tasks }) => {
    return (
        // setTasks([...tasks, {}]) - to add a new task
        <>
            {tasks.map((task) => (
                <h3 key={task.id} task={task}/>
            ))}    
        </>
    )
}

export default Tasks
